  for (var x = 0; x < mapSize; x++) {
    for (var y = 0; y < mapSize; y++) {
      var coordinates = [x, y];
      var currentChar = map[x][y].charAt(0);
      if (currentChar == 'u') {
        // Uninhabited island; highest priority
