// https://stackoverflow.com/a/52366332
function flipInt(n) {
  var digit, result = 0;
  while (n) {
      digit = n % 10;                  //  Get last digit. Ex. 123/10 โ 12.3 โ 3
      result = (result * 10) + digit;  //  Ex. 123 โ 1230 + 4 โ 1234
      n = n/10|0;                      //  Remove last digit. Ex. 123 โ 12.3 โ 12
  }

  return result;
}
